@RunWith(VertxUnitRunner.class)
public class Oauth2TokenTest {

  @Rule
  public RunTestOnContext rule = new RunTestOnContext();

  final static JsonObject keycloakToken = new JsonObject(
    "{\n" +
      "    \"access_token\":\"eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJZTnJLdkhxdkxWSW5BSUI5RDk4bGJlT1hMam9uVlh4T0hnTW5JMUU4dEo4In0..TQ_qC7Zq3Ga3Zbf_eSnaLe0f2bwTyKMwuNJ8CdGRoD4vGaAc3_hIweOFfnCMwQd1kYkn3TbK-tu3z7uLkrpN3snnBl8zCZcH2hueKP9c3x0HdzBXgPmHWraPZdmh6Oe9_PisfjOhlLUW02o2Us_WEAPerFGHHi2uCLQh8em3UiGTNA-p0VhvNTOkkuyQO6ZM0TAGZx5CUqXmBJO6O_rgcSVnRbXmyzpnbWHQNOfnb1SCcYEEOiVRFjBHpT1CVH12Fc475hDTmxUzK7F6CbMOh2lemhRQ-g8qSNuebYeQaqWz9EPOG1M6OV58krMWROxB_zagyo_ZabmvcuyNc4MDsA\",\n" +
      "    \"expires_in\":60,\n" +
      "    \"refresh_expires_in\":1800,\n" +
      "    \"refresh_token\":\"eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJZTnJLdkhxdkxWSW5BSUI5RDk4bGJlT1hMam9uVlh4T0hnTW5JMUU4dEo4In0..Fe2Q2dOpMm0YC31rqBduqJx8GZIGkG2h9xztR2aEWseq_ZwNVwFqX6T15HfGEESUmYzx-sZqAKWizHH93lKmcszkbvttD72gpxflAv3qFZgSW92wMfBH_vC3Mf1W4qFJk8GXYJ6qCyZJ49gtXG5IkzGHFtB0PJsvIQHovBrMZuGM6mgs1EP2MkMbLAOPuv4LDo004ZIMZMe7KJ40QIf34hIMnb-4-At-6tszZZ2UEcED_u2vxx4Jg9YAT3A3JAcwJlHoEkrq-5rSGfULPO4esWINGBeeGnTlTJUOZordaP4vbRLeHL2RQCDTSVkQVN4FBMtuezxibF95JNDStqRcfw\",\n" +
      "    \"token_type\":\"bearer\",\n" +
      "    \"not-before-policy\":0,\n" +
      "    \"session_state\":\"679aa395-49ee-4f19-84b2-ecb6f6327ce2\"\n" +
      "}"
  );

  @Test
  public void keycloakTest(TestContext should) throws Exception {
    User token = User.create(keycloakToken);

    should.assertNotNull(token.get("access_token"));
    should.assertNotNull(token.get("refresh_token"));
    should.assertNull(token.get("accessToken"));
  }

  @Test
  public void testNullScope(TestContext should) throws Exception {
    JsonObject json = new JsonObject(
      "{\n" +
        "    \"access_token\":\"xyz\",\n" +
        "    \"expires_in\":60,\n" +
        "    \"token_type\":\"bearer\",\n" +
        "    \"not-before-policy\":0,\n" +
        "    \"scope\":null\n" +
        "}"
    );

    try {
      User token = User.create(json);
    } catch (RuntimeException e) {
      should.fail(e);
    }
  }
}
